body {
  font-family: 'Sue Ellen Francisco', cursive;
  font-size: 22px;
  line-height: 1.75;
  background-color: #207f96;
  color: #681a1a;
}

a {
  color: #681a1a!important;
}

img {
  max-width: 100%;
  height: auto;
}

.container {
  max-width: 1920px;
  margin: 0 auto 150px;
  /* padding: 60px 15px; */
}

.big-image {
  margin: 0 auto 60px;
  display: block;
}

ul {
  margin: 0;
  padding: 0;
  list-style: none;
}

.menu li {
  margin: 20px;
  padding: 0;
  /* max-width: 150px; */
  position: relative;
}

.menu ul {
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  align-items: flex-end;
}

.container-omino {
  background-image: url(/8ea99d7544e3323ae70198fb4e53b923.gif);
  background-size: contain;
  background-position: center;
  height: 70vh;
  margin-bottom: 30px;
  background-repeat: no-repeat;
}

.menu img {
  max-width: 100px;
}
.note img {
  max-width: 60px;
}

h3 {
  font-size: 32px;
}

.menu .submenu {
  position: absolute;
  width: 200px;
  left: calc( 50% - 100px );
  flex-direction: column;
  text-align: center;
  display: none;
  top: -200px;
  background-color: #207f96;
  padding-top: 5px;
}

.menu .telefono .submenu {
  top: -80px;
  width: 180px;
}

.menu .submenu li {
  width: 100%;
  margin: 0;
}

.menu .submenu li a {
  font-size: 30px;
  line-height: 1.2;
  color: #681a1a;
}

.menu {
  background-color: #207f95;
}

.col-2 .container-text {
  padding: 20px;
}

.container-text p {
  font-size: 28px;
  text-align: justify;
}

@media screen and (min-width: 1025px) {
  .menu {
    position: fixed;
    width: 100%;
    bottom: 0;
    left: 0;
  }
  .menu li:hover > .submenu {
    display: block;
  }

  .col-2 {
    display: flex;
    flex-wrap: wrap;
  }

  .col-2 .container-omino,
  .col-2 .container-text {
    width: 50%;
    padding: 30px 30px 0px 0px;
  }
}

.menu .submenu.menu-open {
  display: block;
}

@media screen and (max-width: 1024px) {
  .menu li {
    margin: 5px;
  }

  .menu img {
    max-width: 60px;
  }

  .note img {
    max-width: 40px;
  }

  .container-text p {
    font-size: 22px;
  }
}

